import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalTeachers: 0,
    totalStudents: 0,
    totalParents: 0
  });
  const [recentConnections, setRecentConnections] = useState([]);
  const [activeSection, setActiveSection] = useState('overview');

  // Vérification de l'authentification admin
  useEffect(() => {
    if (!user || user.role !== 'admin' || user.email !== 'admin@cybermathia.com') {
      navigate('/admin-login');
    }
  }, [navigate, user]);

  // Charger les statistiques
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/admin/stats', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setStats(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des statistiques:', error);
      }
    };

    const fetchRecentConnections = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/admin/connections', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setRecentConnections(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des connexions:', error);
      }
    };

    fetchStats();
    fetchRecentConnections();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <nav className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex">
              <div className="flex-shrink-0 flex items-center">
                <span className="text-2xl font-bold text-red-600">Admin CyberMathIA</span>
              </div>
            </div>
            <div className="flex items-center">
              <span className="text-gray-700 mr-4">Admin</span>
              <button
                onClick={() => {
                  localStorage.clear();
                  navigate('/admin-login');
                }}
                className="text-gray-500 hover:text-gray-700"
              >
                Déconnexion
              </button>
            </div>
          </div>
        </div>
      </nav>

      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 bg-white h-screen shadow-sm">
          <nav className="mt-5 px-2">
            <button
              onClick={() => setActiveSection('overview')}
