import React from 'react';
import { Navigate } from 'react-router-dom';

const PrivateRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));

  if (!token || !user) {
    // Si pas de token ou pas d'utilisateur, rediriger vers la page de connexion
    return <Navigate to="/login" replace />;
  }

  // Vérifier que l'utilisateur accède à la bonne section selon son rôle
  const path = window.location.pathname;
  const role = user.role;

  // Vérifier les accès selon le rôle
  if (
    (role === 'admin' && !path.startsWith('/admin')) ||
    (role === 'teacher' && !path.startsWith('/teacher')) ||
    (role === 'parent' && !path.startsWith('/parent')) ||
    (role === 'manager' && !path.startsWith('/manager'))
  ) {
    // Rediriger vers le bon dashboard selon le rôle
    switch (role) {
      case 'admin':
        return <Navigate to="/admin/dashboard" replace />;
      case 'teacher':
        return <Navigate to="/teacher/dashboard" replace />;
      case 'parent':
        return <Navigate to="/parent/dashboard" replace />;
      case 'manager':
        return <Navigate to="/manager/dashboard" replace />;
      default:
        return <Navigate to="/login" replace />;
    }
  }

  // Si tout est OK, afficher le composant protégé
  return children;
};

export default PrivateRoute;
