import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import ParentDashboard from './components/ParentDashboard';
import TeacherDashboard from './components/TeacherDashboard';
import ManagerDashboard from './components/ManagerDashboard';
import HeroSection from './components/HeroSection';
import PrivateRoute from './components/PrivateRoute';

function App() {
  // Vérifier si l'utilisateur est connecté avec un token valide
  const isAuthenticated = () => {
    try {
      const token = localStorage.getItem('token');
      const userStr = localStorage.getItem('user');

      // Si l'un des deux est manquant, on considère que l'utilisateur n'est pas connecté
      if (!token || !userStr) {
        return false;
      }

      // Vérifier que user est un objet JSON valide avec un rôle
      const user = JSON.parse(userStr);
      if (!user || !user.role) {
        return false;
      }

      // Vérifier que le token n'est pas expiré (si nécessaire)
      // ... logique de vérification du token ...

      return true;
    } catch (error) {
      return false;
    }
  };

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        <Routes>
          {/* Routes publiques */}
          <Route path="/" element={<HeroSection />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/admin/login" element={<AdminLogin />} />

          {/* Routes protégées */}
          <Route path="/admin/dashboard/*" element={
            <PrivateRoute>
              <AdminDashboard />
            </PrivateRoute>
          } />
          <Route path="/parent/dashboard/*" element={
            <PrivateRoute>
              <ParentDashboard />
            </PrivateRoute>
          } />
          <Route path="/teacher/dashboard/*" element={
            <PrivateRoute>
              <TeacherDashboard />
            </PrivateRoute>
          } />
          <Route path="/manager/dashboard/*" element={
            <PrivateRoute>
              <ManagerDashboard />
            </PrivateRoute>
          } />

          {/* Redirection par défaut */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
